mapper.update:
update bl_about set
version = version + 1

    ,about_title = #{aboutTitle}


    ,about_content = #{aboutContent}


    ,about_read = #{aboutRead}

where about_id = #{aboutId}
and version = #{version}

update bl_about set
deleted = 1
where about_id = #{id}

update bl_about set
version = version + 1,
enable = #{enable}
where about_id = #{aboutId}
and version = #{version}

mapper.select:
select about_id, about_title, about_read, created_time, update_time, enable from bl_about where deleted = 0
and about_title like CONCAT('%', #{params.aboutTitle}, '%')
order by ${sortColumn} ${sortMethod}
limit #{index}, #{pageSize}
